import { useNavigate } from "react-router-dom"
import { useEffect, useState } from "react"
import { useForm } from 'react-hook-form'
import * as yup from 'yup'
import { Footer } from '../components/footer.js'
import shoe from '../images/shoe.png'
import minishoe from '../images/minishoe.png'
import { yupResolver } from '@hookform/resolvers/yup'
import { Color } from "../components/Color.js"
import axios from "axios"


export const CreateProduct = () => {
    const [colorsList, setColorsList] = useState([])
    const [inputValue, setInputValue] = useState("")
    const [displayImage, setDisplayImage] = useState("")
    const [image, setImage] = useState("")
    const [newColor, setNewColor] = useState("")
    const [isImage, setIsImage] = useState(false)
    const [product, setProduct] = useState({
        name:'',
        price: 0,
        type: '',
        quantity: 1
    })

    const shema = yup.object().shape({
        name: yup.string().min(4).required("You must add the name of the product"),
        price: yup.number().integer().positive().required("You must add the price"),
        type: yup.string().min(4).required("You must add the type of the product"),
        quantity: yup.number().integer().positive().min(1).required("You must add the Stock Quantity")
    })
    const { register, handleSubmit, formState: { errors } } = useForm({
        resolver: yupResolver(shema)
    })
    // useEffect
    const onSubmit = (data) => {
        setProduct({...product, name: data.name, price:data.price, type:data.type, quantity: data.quantity})
        CreateProduct()

    }
    const CreateProduct = async () => {
        await axios.put("http://localhost:3001/admin/product", {...product, colors: colorsList})
    }
    useEffect( () => {
        axios.get("http://localhost:3001/admin/product").then( (res) => {
            console.log(res.data)
        })
    }, [])
    console.log(product)

    const handleChange = (event) => {
        setNewColor(event.target.value)
        setInputValue(event.target.value)
    }
    const addColor = () => {
        if ( inputValue !== "") {
        const color = {
            id: colorsList.length === 0 ? 1 : colorsList[colorsList.length - 1].id + 1,
            colorName: newColor
        }

        setColorsList([...colorsList, color])
    }
        setInputValue("")

    }



    const deleteColor = function (id) {
        console.log(id)
        const newColorsList = colorsList.filter((color) => {
            if (color.id === id) {
                return false
            } else {
                return true
            }
        })
        setColorsList(newColorsList)
    }

    const handleImage = (e) => {

        const selectedFile = e.target.files[0];
        setImage(e.target.files[0])

        if (selectedFile) {
            const imageUrl = URL.createObjectURL(selectedFile);
            setDisplayImage({ photoURL: imageUrl });
        }
        setIsImage(true)
    }
    // sending the datata to the api
    const uploadProduct = (event) => {
        event.preventDefault();

    }

    return (
        <div className='main-container'>
            <div className="prod-container">
                <div className="form-container">
                <form onSubmit={handleSubmit(onSubmit)}>
                        <h4>Product Name</h4>
                        <input type="text" placeholder='Addidas Ultra Boost' {...register("name")} />
                        <p>{errors.name?.message}</p>
                        <h4>Price of the Product</h4>
                        <input type="number" placeholder='the price' {...register("price")} />
                        <p>{errors.price?.message}</p>
                        <h4>Type</h4>
                        <input type="text" placeholder='Sneaker' {...register("type")} />
                        <p>{errors.type?.message}</p>
                        <h4>Stock Quantity</h4>
                        <input type="number" placeholder='How much we have?' {...register("quantity")} />
                        <p>{errors.quantity?.message}</p>
                        <h4>Colors</h4>
                        {/* the colors section */}
                        <div className="addColor">
                            <input onChange={handleChange} value={inputValue} type="text" />
                            <div onClick={addColor} id="addColor">Add Colors</div>
                        </div>
                        <div className='ColorsList'>
                            {colorsList.map((color) => {
                                return <Color colorName={color.colorName} id={color.id} deleteColor={deleteColor} />
                            })}
                        </div>
                        <h4>Upload The Photo of the Product here</h4>
                        <input type="file" name='file' onChange={handleImage} />

                        <div className="btns">
                            <input type="submit" value="Upload" id="upload-btn" onSubmit={uploadProduct}/>
                            <input type="submit" value="Update" id="update-btn" />
                            <input type="submit" value="Delete" id="d-btn" />
                            <input type="submit" value="Cancel" id="c-btn" />

                        </div>

                    </form>
                </div>
                {/* graphic stuff for the product */}
                <div className="prod-graphic">

                   {
                    isImage ? ( 
                        <>
                    <h4>The Photo that you upload it</h4>
                    <div className='img-container'>
                        <img src={displayImage.photoURL} alt="the image" />
                    </div>
                    </>
                    ) : ( 
                        <>
                            <h3>You didn't Upload any Photo</h3>
                        </>

                     )
                   }





                    </div>


            </div>


            <Footer />
        </div>

    )
}
